#include "test.h"
#include "pointers.h"

static void test_ptr_sum_basic(void) {
    int arr[] = {1, 2, 3, 4, 5};
    TEST_ASSERT_EQ(ptr_sum(arr, 5), 15);
}

static void test_ptr_sum_single(void) {
    int arr[] = {42};
    TEST_ASSERT_EQ(ptr_sum(arr, 1), 42);
}

static void test_ptr_find_present(void) {
    int arr[] = {10, 20, 30, 40};
    int *p = ptr_find(arr, 4, 30);
    TEST_ASSERT(p != NULL);
    TEST_ASSERT_EQ(*p, 30);
}

static void test_ptr_find_absent(void) {
    int arr[] = {10, 20, 30};
    int *p = ptr_find(arr, 3, 99);
    TEST_ASSERT(p == NULL);
}

static void test_ptr_reverse_basic(void) {
    int arr[] = {1, 2, 3, 4, 5};
    ptr_reverse(arr, 5);
    TEST_ASSERT_EQ(arr[0], 5);
    TEST_ASSERT_EQ(arr[2], 3);
    TEST_ASSERT_EQ(arr[4], 1);
}

static void test_ptr_reverse_single(void) {
    int arr[] = {7};
    ptr_reverse(arr, 1);
    TEST_ASSERT_EQ(arr[0], 7);
}

int main(void) {
    RUN_TEST(test_ptr_sum_basic);
    RUN_TEST(test_ptr_sum_single);
    RUN_TEST(test_ptr_find_present);
    RUN_TEST(test_ptr_find_absent);
    RUN_TEST(test_ptr_reverse_basic);
    RUN_TEST(test_ptr_reverse_single);
    TEST_SUMMARY();
}
